$(window).load(function () {
	new Swiper(".swiper-clients", { slidesPerView: 5, spaceBetween: 50, loop: !0, breakpoints: { 1024: { slidesPerView: 4, spaceBetween: 50 }, 992: { slidesPerView: 3, spaceBetween: 40 }, 768: { slidesPerView: 3, spaceBetween: 30 }, 600: { slidesPerView: 2, spaceBetween: 30 }, 480: { slidesPerView: 1, spaceBetween: 0 } } }), new Swiper(".swiper-testimonials", { speed: 1e3, autoplay: 1e4, slidesPerView: 1, loop: !0 });
	var swiper = new Swiper(".swiper-container.mySwiper", {
		spaceBetween: 10,
		slidesPerView: 4,
		freeMode: true,
		watchSlidesVisibility: true,
		watchSlidesProgress: true,
	});
	var swiper = new Swiper(".swiper-container.mySwiper2", {
		spaceBetween: 10,
		navigation: {
			nextEl: ".swiper-button-next",
			prevEl: ".swiper-button-prev",
		},
		thumbs: {
			swiper: swiper,
		},
	});
	var swiper = new Swiper(".swiper-container.Swiper", {
		spaceBetween: 8,
		slidesPerView: 4,
		freeMode: true,
		watchSlidesVisibility: true,
		watchSlidesProgress: true,
	});
	var swiper = new Swiper(".swiper-container.Swiper2", {
		spaceBetween: 8,
		navigation: {
			nextEl: ".swiper-button-next",
			prevEl: ".swiper-button-prev",
		},
		thumbs: {
			swiper: swiper,
		},
	});
	$('.btn').click(function (event) {
		$('.block').removeClass('active')
		var num = $(this).attr('data-num');
		$('#block' + num).addClass('active')
	});
	$(".btn").on("click", function () {
		$(".btn").removeClass("active");
		$(this).addClass("active");
	});
});